    React.useLayoutEffect(() => {
        navigation.setOptions({
          headerRight : () => ( 
            <Ionicons
            style={{marginRight:30}}
              name={iconname}
              size={45}
              color={iconcolor}
              onPress={() => {editPressed();}}
            />
      )
        })
      }, [navigation,{}]);

    const editPressed = () => {
        if(iconname === "create"){
            setIconname("checkmark");
            setIconcolor('#00A300');
            setEditVar(true);
        }else{
            setIconname("create");
            setIconcolor('#0000FF');
            setEditVar(false);
        }

    }

return(
    <View>




        <TextInput
        editable={editVar}
        multiline
        numberOfLines={10}
        value={input}
        onChangeText={setInput}
      style={{ height: '70%', borderColor: 'gray', borderWidth: 1 , margin:10}}
    />
    </View>
);
}

export default NotesScreen;
