import React, { useEffect, useRef, useState } from 'react';
import { SafeAreaView, Text, Button } from 'react-native';
import MapView, {Marker} from 'react-native-maps';
import * as Location from 'expo-location';
import { Dimensions } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';

function MapScreen(){

    const mapRef = useRef(null);
    const [currentCoordinates, setCurrentCoordinates] = useState({});
    const [user, setCurrentUser] = useState("");

    var LocationCoords = [];
    var nearestLocations=[];

    const [currRegion, setCurrRegion] = useState({
        latitude: 43.25359093653678,
        longitude: -79.86577030001203,
        latitudeDelta: 0.005,
        longitudeDelta: 0.005
      })

    useEffect(() =>{
        getCurrentLocation();
        AsyncStorage.getItem("username").then((dataFromStorage) => {
            console.log(dataFromStorage);
            setCurrentUser(dataFromStorage);
        })
        .catch((error) => {
            console.log("Error getting username from storage:"+error);
        });

    }, [])

    const getCurrentLocation = () => {

        Location.requestForegroundPermissionsAsync()
        .then(
            (result) => {
                if(result.status === "granted"){
                    return Location.getCurrentPositionAsync({});
                }
                else{
                    alert("You have not granted location permission. Please grant location permission from your device's Settings menu.")
                }
            }
        )
        .then(
            (location) => {

                console.log("Location received: ");
                console.log(location);

                const coordinates = {
                    latitude: location.coords.latitude,
                    longitude: location.coords.longitude
                }
                console.log(coordinates);

                mapRef.current.animateCamera(
                    {center: coordinates}, 2000
                  );

                setCurrentCoordinates(coordinates);


            }
        )
        .catch(
            (error) => {

                console.log("Error while granting permission or while accessing current location: "+error);
            }
        )

    }

    const showCaches = (coordinates) => {

        db.collection("caches").where("postedBy","!=",user).get().then((querySnapshot) => {
            if(querySnapshot.size === 0){
                setMsg("You do not have any caches to find yet");
            }else{

                setMsg("");
                 LocationCoords=[];
                 nearestLocations=[];

                querySnapshot.forEach((doc) => {

                  LocationCoords.push({latitude:parseFloat(doc.data().latitude), longitude:parseFloat(doc.data().longitude),title:doc.data().cacheName,id:doc.id});
                });

                for(let i=0;i<LocationCoords.length;i++){
                   // const dist=getDistance({latitude:43.656290660189946,longitude:-79.38017140235499},{latitude:LocationCoords[i].latitude,longitude:LocationCoords[i].longitude}) * 0.000621371;
                 const dist=getDistance({latitude:coordinates.lat,longitude:coordinates.lng},{latitude:LocationCoords[i].latitude,longitude:LocationCoords[i].longitude}) * 0.000621371;
                    if(!isNaN(dist)){
                            const locationInfo = {
                                location:{
                                    latitude:LocationCoords[i].latitude,
                                    longitude:LocationCoords[i].longitude
                                },
                               distance:dist.toFixed(3),
                               title:LocationCoords[i].title,
                               id:LocationCoords[i].id
                            }
                            nearestLocations.push(locationInfo);

                    }   
                    nearestLocations.sort(function (a, b) {
                        return a.distance - b.distance;
                      }); 
                }
                setNearbyCachesArray(nearestLocations);
                setLoading(false);

               }
            }
          );      
    }

    const mapMoved = (data) =>{
        console.log(data);
        setCurrRegion(data);
    }

    return(
        <SafeAreaView>

            <MapView
            style={{width:Dimensions.get("window").width, height:Dimensions.get("window").height}}
            ref={mapRef}
            initialRegion={currRegion}
            onRegionChangeComplete={mapMoved}
     >

     </MapView>
     <Button title="Current"></Button>
        </SafeAreaView>
    );
}

export default MapScreen;
