import React, { useState }  from 'react';
import { SafeAreaView, Text, View , TextInput, Button} from 'react-native';
import Styles from './Styles';

const Signup = ({navigation, route}) => {

    const {name, setName} = useState("");
    const {email, setEmail} = useState("");
    const {password, setPassword} = useState("");

    const checkIfEmailIdIsValid = () => {

        let reg = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w\w+)+$/;

        if (reg.test(email) === false) {
            console.log("Email is Not Correct");

            return false;
          }
          else {

            console.log("Email is Correct");
            return true;
          }

        }

    const signUpPressed = () => {

        if(name == ""){
            alert("Please enter your name");
        }
        else if(email == "") {
            alert("Please enter your email");
        }

        // else if(checkIfEmailIdIsValid){
        //     alert("Please enter a valid email address");
        // }

        else if (password == ""){
            alert("Please enter your password");
        }

        else{
            alert("sign up successful");
            navigation.popToTop();
        }
    }

    return(
        <SafeAreaView style={Styles.container}>

        <View style={Styles.horizontal_align}>
            <Text>Name: </Text>
            <TextInput style={Styles.input} placeholder="enter your name" value={name} onChangeText={setName} textContentType="name"></TextInput>
        </View>

        <View style={Styles.horizontal_align}>
            <Text>Email: </Text>
            <TextInput style={Styles.input} placeholder="enter your email address" value={email} onChangeText={setEmail} textContentType="emailAddress"></TextInput>
        </View>

        <View style={Styles.horizontal_align}>
            <Text>Password: </Text>
            <TextInput style={Styles.input} placeholder="enter your password" value={password} onChangeText={setPassword} textContentType="password"></TextInput>
        </View>

        <Button title="Sign Up" onPress={signUpPressed}/>


    </SafeAreaView>
    )
}

export default Signup;
