import React, { useEffect, useRef, useState } from 'react';
import { SafeAreaView, Text, Button } from 'react-native';
import MapView, {Marker} from 'react-native-maps';
import * as Location from 'expo-location';
import { Dimensions } from 'react-native';
import {getDistance} from 'geolib';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { db } from './FirebaseManager';

function MapScreen(){

    const mapRef = useRef(null);
    const [currentCoordinates, setCurrentCoordinates] = useState({});
    const [currentUser, setCurrentUser] = useState("");

    const [allCaches, setAllCaches] = useState([]);
    const [nearbyLocations, setNearByLocations] = useState([]);

    const [currRegion, setCurrRegion] = useState({
        latitude: 43.25359093653678,
        longitude: -79.86577030001203,
        latitudeDelta: 0.05,
        longitudeDelta: 0.05
      })

    useEffect(() =>{

        console.log("---------------------------");
        console.log("Getting current user from async storage");

        AsyncStorage.getItem("username").then((dataFromStorage) => {
            setCurrentUser(dataFromStorage);
        })
        .catch((error) => {
            console.log("Error getting username from storage:"+error);
        });

        console.log("Current User : "+currentUser);
        console.log("---------------------------");

        getCurrentLocation();
        getAllCachesFromFirebase();


    }, [])

    const getCurrentLocation = () => {

        console.log("---------------------------");
        console.log("Getting current location");

        Location.requestForegroundPermissionsAsync()
        .then(
            (result) => {
                if(result.status === "granted"){
                    return Location.getCurrentPositionAsync({});
                }
                else{
                    alert("You have not granted location permission. Please grant location permission from your device's Settings menu.")
                }
            }
        )
        .then(
            (location) => {

                console.log("Location received: ");
                console.log(location);

                console.log("Extracting coordinates from received location");

                const coordinates = {
                    latitude: location.coords.latitude,
                    longitude: location.coords.longitude
                }
                console.log(coordinates);
                console.log("Done extracting coordinates from received location");
                setCurrentCoordinates(coordinates);
                showNearbyCaches(coordinates);

                mapRef.current.animateCamera(
                    {center: coordinates}, 2000
                  );

            }
        )
        .catch(
            (error) => {

                console.log("Error while granting permission or while accessing current location: "+error);
            }


        )
        console.log("---------------------------");

    }

    const showNearbyCaches = (newCoordinates) => {

        console.log("---------------------------");
        console.log("Showing nearby caches");

        setNearByLocations([]);

        let loc = [];

        for(let i=0;i<allCaches.length;i++){

          const dist = getDistance({latitude:newCoordinates.latitude, longitude:newCoordinates.longitude}, {latitude:allCaches[i].latitude, longitude:allCaches[i].longitude}) * 0.000621371;

          if(!isNaN(dist)){

             const distance = dist.toFixed(3);
             if (distance <= 25){

                 const locationInfo = {
                    latitude: allCaches[i].latitude,
                    longitude: allCaches[i].longitude,
                     distance: distance,
                     title: allCaches[i].title,
                     id: allCaches[i].id
                 }
                 loc.push(locationInfo);
             }

             }  

         }

         setNearByLocations(loc);

         console.log("NEARBY LOCATIONS"+loc.length);

         console.log("------------------------");

    }

    const getAllCachesFromFirebase = () => {

        console.log("---------------------------");
        console.log("Getting all caches from firebase");

        db.collection("caches").where("postedBy","!=", currentUser).get().then((querySnapshot) => {

            if(querySnapshot.size === 0){
                console.log("No caches available for current user");
            }else{

                const caches = [];

                querySnapshot.forEach((doc) => {

                    caches.push(
                      { latitude: parseFloat(doc.data().latitude),
                        longitude: parseFloat(doc.data().longitude),
                        title: doc.data().cacheName, 
                        id: doc.id,
                        postedBy: doc.postedBy
                    });

                });
                console.log("caches");
                console.log(caches);
                setAllCaches(caches);

               }
            }
          )
          .catch((error) => {
              console.log("Error getting all caches from firebase: "+error);
          });      
          console.log("---------------------------");
    }

    const mapMoved = (data) => {
        console.log(data);
        setCurrRegion(data);
    }

    return(
        <SafeAreaView>

            <MapView
            style={{width:Dimensions.get("window").width, height:Dimensions.get("window").height}}
            ref={mapRef}
            initialRegion={currRegion}
            onRegionChangeComplete={mapMoved}
     >
         {
             nearbyLocations.map((location) => {
                 console.log("mapker");

                <Marker coordinate={{latitude:location.latitude, longitude:location.longitude}}
                title={location.title}
                description="We also make a really good sandwich"></Marker>



             })
         }


     </MapView>
     <Button title="Current"></Button>
        </SafeAreaView>
    );
}

export default MapScreen;
